/**
 *
 * @namespace
 *
 */

var nameSpace = {
  contentDiv: document.getElementById("content"),
  url:
    "https://en.wikipedia.org/w/api.php?&action=opensearch&format=json&origin=*&search=",
  searchText: document.getElementById("searchText"),
  searchWikiEntry: document.getElementById("buttonSearchWikiEntry"),

  search: function() {
    var that = this;

    this.searchWikiEntry.addEventListener("click", function() {
      fetch(that.url + that.searchText.value)
        .then(function(data) {
          return data.json();
        })
        .then(function(articles) {
          that.populate(articles);
        });
    });
  },

  enterListener: function() {
    var that = this;
    document.addEventListener("keypress", function(event) {
      if (event.keyCode === 13) {
        fetch(that.url + that.searchText.value)
          .then(function(data) {
            return data.json();
          })
          .then(function(articles) {
            that.populate(articles);
          });
      }
    });
  },

  populate: function(articles) {
    if (articles.error) {
      var AddSomething = [
        ["add something"],
        ["add something"],
        ["add something"],
        [""]
      ];
      this.createDOMElements(AddSomething);
    } else if (articles[1].length === 0) {
      var nothingFound = [
        ["nothing found"],
        ["Nothing Found"],
        ["Nothing Found"],
        [""]
      ];
      this.createDOMElements(nothingFound);
    } else if (articles[1].length !== 0) {
      this.createDOMElements(articles);
    }
  },

  createDOMElements: function(articles) {
    var that = this;

    if (that.contentDiv.firstChild) {
      while (that.contentDiv.firstChild) {
        that.contentDiv.removeChild(that.contentDiv.firstChild);
      }
    }

    for (var i = 0; i < articles[1].length; i += 1) {
      var article = document.createElement("article");
      var title = document.createElement("h2");
      var header = document.createElement("header");
      var paragraph = document.createElement("p");
      var breakLine = document.createElement("br");
      var anchor = document.createElement("a");

      title.innerText = articles[1][i];
      paragraph.innerText = articles[2][i];

      /* 
       Replaces each escape sequence in the encoded URI component 
       with the character that it represents.
      */
      anchor.href = decodeURIComponent(articles[3][i]);
      anchor.innerText = decodeURIComponent(articles[3][i]);

      header.appendChild(title);
      paragraph.appendChild(breakLine);
      paragraph.appendChild(anchor);

      article.appendChild(header);
      article.appendChild(paragraph);

      this.contentDiv.appendChild(article);
    }
  }
};

nameSpace.search();
nameSpace.enterListener();


